// client/src/components/ReportCard.js
import React from "react";

function ReportCard({ report }) {
  if (!report) return null;

  // Defensive defaults
  const {
    name = "—",
    pan = "—",
    phone = "—",
    creditScore = "—",
    totalAccounts = 0,
    activeAccounts = 0,
    closedAccounts = 0,
    currentBalance = 0,
    securedBalance = 0,
    unsecuredBalance = 0,
    recentEnquiries = 0,
  } = report;

  const accounts = Array.isArray(report.accounts) ? report.accounts : [];

  return (
    <div className="report-card">
      <h2>{name} ({pan})</h2>
      <p>Phone: {phone}</p>
      <p>Credit Score: {creditScore}</p>

      <h3>Summary</h3>
      <ul>
        <li>Total Accounts: {totalAccounts}</li>
        <li>Active: {activeAccounts}</li>
        <li>Closed: {closedAccounts}</li>
        <li>Current Balance: ₹{currentBalance}</li>
        <li>Secured: ₹{securedBalance}</li>
        <li>Unsecured: ₹{unsecuredBalance}</li>
        <li>Recent Enquiries: {recentEnquiries}</li>
      </ul>

      <h3>Accounts</h3>
      {accounts.length === 0 ? (
        <p style={{ color: "#ccc" }}>No account details available.</p>
      ) : (
        accounts.map((acc, i) => (
          <div key={i} className="account-entry">
            <p><strong>{acc.type || "—"}</strong> - {acc.bank || "—"}</p>
            <p>Account #: {acc.accountNumber || "—"}</p>
            <p>Balance: ₹{acc.currentBalance ?? 0}, Overdue: ₹{acc.amountOverdue ?? 0}</p>
            <p>Address: {acc.address || "—"}</p>
          </div>
        ))
      )}
    </div>
  );
}

export default ReportCard;
